#region UTILS
Function Base64ToImage{
    param ([string]$s)

    $bytes = [Convert]::FromBase64String($s)
    return [System.Drawing.Bitmap]::new([System.IO.MemoryStream]::new($bytes, 0, $bytes.Length))
}
Function Base64ToIcon{
    param ([string]$s)

    return [System.Drawing.Icon]::FromHandle((Base64ToImage($s)).GetHIcon())
}
Function log {
    param
    (
        [Parameter(Mandatory=$true, Position=0)]
        [string] $prefix,
        [Parameter(Mandatory=$true, Position=1)]
        [string] $message
    )
    $logFilePath = $env:LOCALAPPDATA.ToString() + "\MEDICARE\logs.txt"
    if((Test-Path $logFilePath) -eq $false) { New-Item -Path $logFilePath -ItemType File }
    Write-Output "[$prefix - $(Get-Date -Format "dd/MM/yyyy HH:mm:ss")] : $message" >> $logFilePath
}
