            $index = $i-$len/2
            $pbX = 290
            $pbY = $defaultPbY
            $lbX = $pbX+35
        }
    
        $pb = New-Object System.Windows.Forms.PictureBox -Property @{
            Name = "PictureBoxStep$i"
            Size = New-Object System.Drawing.Size -Property @{
